import PaintingList from "./PaintingList"
import { useState } from "react";

import { useEffect } from "react";



const Gallery = ({radioValue, name, author}) => {

    const [paintings, setPaintings] = useState();
    const [isAll, setIsAll] = useState(true);
    console.log(radioValue.radioValue);
    useEffect(() => {
        if(radioValue !== 'all'){
            setIsAll(false);
        } else {
            setIsAll(true);
        }

    }, [radioValue, name, author]);
    return ( 
        <div className="gallery">
            {!isAll && <PaintingList 
                paintings={paintings.filter((painting) => painting.genre === radioValue && painting.title.toLowerCase().includes(name) && painting.author.toLowerCase().includes(author))}
                title= {radioValue +" Paintings"} />}
            {isAll && <PaintingList paintings={paintings.filter((painting) => painting.title.toLowerCase().includes(name) && painting.author.toLowerCase().includes(author))} title="All Paintings" />}
        </div>
     );
}

export default Gallery;
