import PaintingList from "./PaintingList"
import { useState } from "react";
import painting1 from './images/painting1.jpeg'
import painting2 from './images/painting2.jpg'
import painting3 from './images/painting3.jpg'
import painting4 from './images/painting4.png'
import painting5 from './images/painting5.jpg'
import painting6 from './images/painting6.jpg'
import painting7 from './images/painting7.jpg'
import painting8 from './images/painting8.jpg'
import painting9 from './images/painting9.jpg'
import { useEffect } from "react";



const Home = (radioValue) => {

    const [paintings, setPaintings] = useState([
        { title: 'Starry Night', source: painting1, author: 'Van Gogh', genre: 'Impressionism', id: 1 },
        { title: 'Cool Castle', source: painting2, author: 'My Buddy Eric', genre: 'Impressionism', id: 2 },
        { title: 'Christmas!', source: painting3, author: 'someone', genre: 'Impressionism', id: 3 },
        { title: 'Sleepy', source: painting4, author: 'Inigo Montoya', genre: 'Modern', id: 4 },
        { title: 'Autumn Promenade', source: painting5, author: 'Jean Paul-Sartre', genre: 'Impressionism', id: 5 },
        { title: 'Mountain', source: painting6, author: 'Gustave H.', genre: 'Impressionism', id: 6 },
        { title: 'Space Odyssey', source: painting7, author: 'Dave', genre: 'Modern', id: 7 },
        { title: 'Aphrodite', source: painting8, author: 'A Greek Guy?', genre: 'Classical', id: 8 },
        { title: 'Guernica But Ugly', source: painting9, author: 'Napoleon', genre: 'Classical', id: 9 }
      ]);
    const [isAll, setIsAll] = useState(true);
    console.log(radioValue.radioValue);
    useEffect(() => {
        if(radioValue.radioValue !== 'all'){
            setIsAll(false);
        } else {
            setIsAll(true);
        }
    }, [radioValue]);
    return ( 
        <div className="home">
            {!isAll && <PaintingList paintings={paintings.filter((painting) => painting.genre === radioValue.radioValue)} title= {radioValue.radioValue +" Paintings"} />}
            {isAll && <PaintingList paintings={paintings} title="All Paintings" />}
        </div>
     );
}

export default Home;
