import {
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  makeStyles,
  Grid,
  IconButton,
} from "@material-ui/core";
import { InfoOutlined } from "@material-ui/icons";
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import { useHistory } from "react-router-dom";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

const PaintingList = ({ paintings, title }) => {
  const classes = useStyles();
    const history = useHistory();

  return (
    <div className="painting-list">
      <h2>{title}</h2>
      <div className="list">
        <Grid
          container
          spacing={2}
          direction="row"
          justify="flex-start"
          alignItems="flex-start"
        >
          {paintings.map((painting) => (
            <Grid item xs={12} sm={6} md={3} key={painting.id}>
              <Card className={classes.root}>
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={painting.source}
                    title="Contemplative Reptile"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                      {painting.title}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      component="p"
                    >
                      by {painting.author}
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button size="small" color="primary" onClick={() => history.push('/' + painting.id)}>
                    Learn More
                  </Button>
                  <IconButton
                    edge="start"
                    className="menu"
                    color="inherit"
                    aria-label="menu"
                  >
                    <FavoriteBorderIcon />
                  </IconButton>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </div>
    </div>
  );
};

export default PaintingList;
