import './App.css';
import Navbar from './Navbar';
import Home from './Home';
import Filter from './Filter';
import {Grid} from "@material-ui/core"
import { useState } from 'react';

function App() {

  const [radioValue, setRadioValue] = useState('all');
  const [name, setName] = useState('');
  const [author, setAuthor] = useState('');
  console.log(radioValue);
  console.log(name);

  const handleChange = (event) => {
    setRadioValue(event.target.value);
  };
  const nameChange = (event) => {
    setName(event.target.value);
  };

  const authorChange = (event) => {
    setAuthor(event.target.value);
  };
  return (
    <div className="App">
      <Navbar />
      <div className="content">
      <Grid
                container
                spacing={2}
                direction="row"
                justify="flex-start"
                alignItems="flex-start"
            >
          <Grid item xs={12} sm={6} md={3} key="1">
            <Filter radioValue={radioValue} handleChange={handleChange} name={name} author={author} nameChange={nameChange} authorChange={authorChange} />
          </Grid>
          <Grid item xs={12} sm={6} md={8} key="2">

            <Home radioValue={radioValue} name={name} author={author} />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default App;
